							<view @tap="goIntegralDetails" class="new-top-arrow">积分明细><text
									class="iconfont icon-right"></text>
							</view>
						</view>
						<view class="new-top-score-wrap">
							<view class="new-top-score">通用积分:{{credit.credits}}<text bindtap="showTip3"
									class="iconfont icon-xinxi"></text>
							</view>
						</view>
