				</block>
				<block v-if="item.action=='post'">
					<image class="item_icon" mode="widthFix" :src="images.create_question"></image>
				</block>
				<block v-if="item.action=='read'">
					<image class="item_icon" mode="widthFix" :src="images.view_question"></image>
				</block>
				<block v-if="item.action=='comment'">
					<image class="item_icon" mode="widthFix" :src="images.comment_answer"></image>
				</block>
				<!-- #ifdef MP -->
				<block v-if="item.action=='invite'">
					<image class="item_icon" mode="widthFix" :src="images.invite_user"></image>
				</block>
				<!-- #endif -->
				<block v-if="item.action=='adview'">
					<image class="item_icon" mode="widthFix" :src="images.special_gift"></image>
				</block>
				<view class="_info">
					<view class="_title">{{item.name}}
						<image class="score-type" mode="widthFix" :src="images.common_icon"></image>
					</view>
					<view class="_second_title">{{item.description}}
					</view>
				</view>
