					<block v-if="item.action=='invite'">
						<button open-type="share" class="btn btn-primary btn-small">去邀请</button>
					</block>
					<!-- #endif -->
					<block v-if="item.action=='adview'">
						<button @tap="adView" class="btn btn-primary btn-small">去观看</button>
					</block>

				</view>
			</view>
