<script>
	import config from '@/api/config.js';
	import API from '@/api/api.js';
	let videoAd = null;
	var adReward = null;
	var app = getApp();
	export default {
		data() {
			return {
				user: '',
				credit: '',
				archive: [],
				"images": {
					"info": "data:image/png;base64,",

					"answer": "data:image/png;base64,",
					"answer_for_question_owner": "data:image/png;base64,",
					"answer_like": "data:image/png;base64,",
					"best_answer_for_question_owner": "data:image/png;base64,",
					"best_answer_for_responder": "data:image/png;base64,",
					"comment_answer": "data:image/png;base64,",
					"create_question": "data:image/png;base64,",
					"follow_question": "data:image/png;base64,",
					"view_question": "data:image/png;base64,",
					"wechat": "data:image/png;base64,",
					"invite_user": "data:image/png;base64,",
					"new_user": "data:image/png;base64,",
					"special_gift": "data:image/png;base64,",
					"common_icon": "data:image/png;base64,",
					"first_add": "data:image/png;base64,",



				},
			}
		},
		onLoad() {
			this.onLoadRewardedVideoAd();
		},
		onShow: function() {
			let user = app.globalData.user
			if (!user) {
				user = '';
				uni.navigateTo({
					url: '/pages/login/login',
				})
			} else {
				this.getUserMineInfo();
				this.getachire();

			}
		},
		methods: {
			goMall: function() {
				uni.navigateTo({
					url: 'intergralshop/intergralshop'
				})
			},
			onLoadRewardedVideoAd: function() {
				let that = this
				let datetime = Date.now()
				let expired = uni.getStorageSync('vd_expire')
				let videoAdLogs = uni.getStorageSync('videoAdLogs') || []
				API.videoAdsense().then(res => {
						if (res.status === 200) {
							if (uni.createRewardedVideoAd) {
								videoAd = uni.createRewardedVideoAd({
									adUnitId: res.data.code
								})
								videoAd.onLoad(() => {
									console.log('onLoad Video Ad')
								})
								videoAd.onError((err) => {
									console.log(err)
								})
								videoAd.onClose((res) => {
									if (res && res.isEnded) {
										API.updateCredit({
												action: "adview",
												uid: that.user.id,
											}).then(res => {
												that.$refs.uToast.show({
													title: res.message,
													type: 'success',
												})
											})
											.catch(err => {
												that.$refs.uToast.show({
													title: res.message,
													type: 'error',
												})
											})

									}
								})
							} else {
								this.isVideoAds = false

							}
						}
					})
					.catch(err => {
						console.log(err)
					})
			},
			sign: function() {
				let that = this;
				API.updateCredit({
						action: "sign"
					}).then(res => {
						that.$refs.uToast.show({
							title: res.message,
							type: 'success',
						})
						if (res.status == 201) {
							uni.navigateTo({
								url: '/pages/credits/rank'
							})
						}
					})
					.catch(err => {
						console.log(err)
					})
			},

			gotoPost: function() {
				uni.navigateTo({
					url: '/pages/creat/topic/topic'
				})
			},
			gotoIndex: function() {
				uni.switchTab({
					url: '/pages/home/home'
				})
			},
			goExchange: function() {
				uni.navigateTo({
					url: 'exchanges/exchanges'
				})
			},
			getUserMineInfo: function() {
				let that = this;
				API.checkUser().then(res => {
						if (res.status == 200) {
							API.getMineInfo().then(res => {
									console.log(res);
									that.user = res.user;
									that.account = res.account;
									that.social = res.social;
									that.credit = res.credit;
									that.subscribe = res.subscribe;
									that.honour = res.honour;
								})
								.catch(err => {
									console.log(err)
								})
						} else {
							uni.showModal({
								title: '温馨提示',
								content: '检测出授权已过期, 是否注销授权?',
								success: function(res) {
									if (res.cancel) {
										that.$refs.uToast.show({
											title: "取消注销",
											type: 'error',
										})
									} else {
										that.bindLogout()
									}
								},
								fail: function(err) {
									console.log(err)
								}
							})
						}
					})
					.catch(err => {
						console.log(err)
					})
			},
			goIntegralDetails: function() {
				uni.navigateTo({
					url: '/pages/integral/information/information'
				})
			},

			adView: function() {
				if (videoAd) {
					videoAd.show().catch(() => {
						videoAd.load().then(() => videoAd.show())
							.catch(err => {
								console.log('激励视频广告显示错误', err)
							})
					})
				}
			},
			

			getachire: function() {
				API.getArchive().then(res => {
					console.log(res);
					this.archive = res.data
				})
			}
		}
	}
</script>

